        public static ML.Result getPlantillas(int IdUser) {
            ML.Result result = new ML.Result();
            try {
                using (DL.RH_DesEntities context = new DL.RH_DesEntities()) {
                    var idParametro = 1;

                    var query = context.Plantillas.SqlQuery("Select * FROM Plantillas"+
                    " INNER JOIN FooterPlantilla ON Plantillas.IdFooterPlantilla = FooterPlantilla.IdFooterPlantilla "+
                    " INNER JOIN HeaderPlantilla ON Plantillas.IdHeaderPlantilla = HeaderPlantilla.IdHeaderPlantilla "+
                    " INNER JOIN DetallePlantilla ON Plantillas.IdDetallePlantilla = DetallePlantilla.IdDetallePlantilla "+
                    " INNER JOIN TipoEstatus ON Plantillas.IdEstatus = TipoEstatus.IdEstatus " +
                    " INNER JOIN TipoPlantilla ON Plantillas.IdTipoPlantilla = TipoPlantilla.IdTipoPlantilla" +
                    " WHERE Plantillas.IdEstatus = {0} and Plantillas.IdTipoPlantilla IN (1,2) ", idParametro).ToList();
                    //Sequita el filtro de Predeterminadas para que salgan las Plantillas activas
                    //" WHERE Plantillas.IdEstatus = {0} and  Plantillas.UsuarioCreacion = 'Predeterminada'", idParametro).ToList();
                    //context.PlantillasGetAll().ToList();
                    result.Objects = new List<object>();
                    result.ListadoDePlantillasPredefinidas = new List<ML.Plantillas>();
                    if (query != null)
                    {
                        foreach (var obj in query)
                        {
                            ML.Plantillas plantillas = new ML.Plantillas();
                            plantillas.IdPlantilla = obj.IdPlantilla;
                            plantillas.TipoEstatus = new ML.TipoEstatus();
                            plantillas.TipoEstatus.Descripcion = obj.TipoEstatus.Descripcion.ToString();                            
                            plantillas.Nombre = obj.Nombre;
                            plantillas.DetallePlantilla = new ML.DetallePlantilla();
                            //plantillas.DetallePlantilla.ThumbImage = obj.DetallePlantilla.ThumbImage.ToString();
                            plantillas.DetallePlantilla.ThumbImage = obj.DetallePlantilla.ThumbImage != null ? obj.DetallePlantilla.ThumbImage.ToString() : "<img class=\"img-fluid\" src=\"data:image/jpeg;base64,\" data-filename=\"PlantillaSIN.jpg\">";
                            plantillas.TipoPlantilla = new ML.TipoPlantilla();
                            plantillas.TipoPlantilla.IdTipoPlantilla = obj.TipoPlantilla.IdTipoPlantilla;
                            plantillas.TipoPlantilla.Nombre = obj.TipoPlantilla.Nombre.ToString();
                            result.Objects.Add(plantillas);
                            result.ListadoDePlantillasPredefinidas.Add(plantillas);
                            result.Correct = true;

                        }
                    }
                    ML.Result resultDefinidas = new ML.Result();
                    resultDefinidas = getPlantillasDefinidas();
                    ML.Result resultPorUsuario = new ML.Result();
                    resultPorUsuario = getPlantillasByIdUser(IdUser);
                    ML.Result resultUltimas = new ML.Result();
                    resultUltimas = getPlantillasLastPublisher(IdUser);
                    result.ListadoDePlantillasPorUsuario = resultPorUsuario.ListadoDePlantillasPorUsuario;
                    result.ListadoDePlantillasUltimas = resultUltimas.ListadoDePlantillasUltimas;
                    result.ListadoDePlantillas = resultDefinidas.ListadoDePlantillas;
                    
                }

            }
            catch (Exception aE)
            {
                result.ex = aE;
                result.ErrorMessage = aE.Message;
                result.Correct = false;
            }
            return result;
        }
        public static ML.Result getPlantillasByIdUser(int IdUser)
        {
            ML.Result result = new ML.Result();
            try {
                using (DL.RH_DesEntities context = new DL.RH_DesEntities())
                {
                    var idParametro = 1;
                    string idUsuarioAdmin = IdUser.ToString();
                    var query = context.Plantillas.SqlQuery("Select * FROM Plantillas" +
                    " INNER JOIN FooterPlantilla ON Plantillas.IdFooterPlantilla = FooterPlantilla.IdFooterPlantilla " +
                    " INNER JOIN HeaderPlantilla ON Plantillas.IdHeaderPlantilla = HeaderPlantilla.IdHeaderPlantilla " +
                    " INNER JOIN DetallePlantilla ON Plantillas.IdDetallePlantilla = DetallePlantilla.IdDetallePlantilla " +
                    " INNER JOIN TipoEstatus ON Plantillas.IdEstatus = TipoEstatus.IdEstatus " +
                    " INNER JOIN TipoPlantilla ON Plantillas.IdTipoPlantilla = TipoPlantilla.IdTipoPlantilla" +
                    " WHERE Plantillas.IdEstatus = {0} and Plantillas.UsuarioCreacion = {1}", idParametro,idUsuarioAdmin).ToList();

                    result.ListadoDePlantillasPorUsuario = new List<ML.Plantillas>();
                    if (query != null)
                    {
                        foreach (var obj in query)
                        {
                            ML.Plantillas plantillas = new ML.Plantillas();
                            plantillas.IdPlantilla = obj.IdPlantilla;
                            plantillas.TipoEstatus = new ML.TipoEstatus();
                            plantillas.TipoEstatus.Descripcion = obj.TipoEstatus.Descripcion.ToString();
                            plantillas.Nombre = obj.Nombre;
                            plantillas.DetallePlantilla = new ML.DetallePlantilla();
                            plantillas.DetallePlantilla.ThumbImage = obj.DetallePlantilla.ThumbImage != null ? obj.DetallePlantilla.ThumbImage.ToString() : "<img class=\"img-fluid\" src=\"data:image/jpeg;base64,\" data-filename=\"PlantillaSIN.jpg\">";
                            plantillas.TipoPlantilla = new ML.TipoPlantilla();
                            plantillas.TipoPlantilla.IdTipoPlantilla = obj.TipoPlantilla.IdTipoPlantilla;
                            plantillas.TipoPlantilla.Nombre = obj.TipoPlantilla.Nombre.ToString();
                            //result.Objects.Add(plantillas);
                            result.ListadoDePlantillasPorUsuario.Add(plantillas);
                            result.Correct = true;
                        }
                    }

                }
            }
            catch(Exception aE)
                {
                result.ex = aE;
                result.ErrorMessage = aE.Message;
                result.Correct = false;
            }
            return result;
        }
