        public static ML.Result getPlantillaById(int IdPlantilla)
        {
            ML.Result result = new ML.Result();
            try {
                using (DL.RH_DesEntities context = new DL.RH_DesEntities()) {
                   var idParametro = 1;
                    var query = context.Plantillas.SqlQuery("Select * FROM Plantillas" +
                    " INNER JOIN FooterPlantilla ON Plantillas.IdFooterPlantilla = FooterPlantilla.IdFooterPlantilla " +
                    " INNER JOIN HeaderPlantilla ON Plantillas.IdHeaderPlantilla = HeaderPlantilla.IdHeaderPlantilla " +
                    " INNER JOIN DetallePlantilla ON Plantillas.IdDetallePlantilla = DetallePlantilla.IdDetallePlantilla " +
                    " INNER JOIN BodyPlantilla on Plantillas.IdBodyPlantilla = BodyPlantilla.IdBodyPlantilla "+
                    " INNER JOIN TipoEstatus ON Plantillas.IdEstatus = TipoEstatus.IdEstatus " +
                    " INNER JOIN TipoPlantilla ON Plantillas.IdTipoPlantilla = TipoPlantilla.IdTipoPlantilla" +
                    " INNER JOIN Aling A1 ON HeaderPlantilla.LogoAlinea = A1.IdAling " +
                    " INNER JOIN Aling A2 ON FooterPlantilla.LogoAlinea = A2.IdAling "+
                    " WHERE Plantillas.IdEstatus = {0} and Plantillas.IdPlantilla = {1}", idParametro,IdPlantilla).ToList();
                    result.Objects = new List<object>();
                    result.EditaPlantillas = new ML.Plantillas();
                    if (query != null) {
                        foreach (var obj in query)
                        {
                            ML.Plantillas plantillas = new ML.Plantillas();
                            plantillas.IdPlantilla = obj.IdPlantilla;
                            plantillas.Nombre = obj.Nombre;
                            /***Tipo estatus****/
                            plantillas.TipoEstatus = new ML.TipoEstatus();
                            plantillas.TipoEstatus.Descripcion = "Activo";
                            /***Detalle Plantilla***/
                            plantillas.DetallePlantilla = new ML.DetallePlantilla();
                            plantillas.DetallePlantilla.IdDetallePlantilla = obj.DetallePlantilla.IdDetallePlantilla;
                            plantillas.DetallePlantilla.Nombre = obj.DetallePlantilla.Nombre;
                            plantillas.DetallePlantilla.CodeHTML = obj.DetallePlantilla.CodeHTML;
                            plantillas.DetallePlantilla.ImagenIco = obj.DetallePlantilla.ImagenIco != null ? obj.DetallePlantilla.ImagenIco.ToString():"";
                            plantillas.DetallePlantilla.ThumbImage = obj.DetallePlantilla.ThumbImage;
                            plantillas.DetallePlantilla.Color1 = obj.DetallePlantilla.Color1 != null ? obj.DetallePlantilla.Color1.ToString():"";
                            plantillas.DetallePlantilla.Color2 = obj.DetallePlantilla.Color2 != null ? obj.DetallePlantilla.Color2.ToString() :"";
                            plantillas.DetallePlantilla.IdPlantillaDefinida = Convert.ToInt32(obj.DetallePlantilla.IdPlantillaDefinida);
                            //plantillas.DetallePlantilla.ThumbImage = obj.DetallePlantilla.ThumbImage == null ? "":obj.DetallePlantilla.ThumbImage.ToString();
                            plantillas.DetallePlantilla.ThumbImage = obj.DetallePlantilla.ThumbImage != null ? obj.DetallePlantilla.ThumbImage.ToString() : "";
                            /***Body Plantilla***/
                            plantillas.BodyPlantilla = new ML.BodyPlantilla();
                            plantillas.BodyPlantilla.IdBodyPlantilla = obj.BodyPlantilla.IdBodyPlantilla;
                            plantillas.BodyPlantilla.CodeHTML = obj.BodyPlantilla.CodeHTML;
                            plantillas.BodyPlantilla.Color1 = obj.BodyPlantilla.Color1 != null ? obj.BodyPlantilla.Color1.ToString():"" ;
                            plantillas.BodyPlantilla.Color2 = obj.BodyPlantilla.Color2 != null ? obj.BodyPlantilla.Color2.ToString():"";
                            plantillas.BodyPlantilla.ImagenFondo = obj.BodyPlantilla.ImagenFondo != null ? obj.BodyPlantilla.ImagenFondo.ToString():"";
                            /***Tipo de Plantilla ***/
                            plantillas.TipoPlantilla = new ML.TipoPlantilla();
                            plantillas.TipoPlantilla.IdTipoPlantilla = obj.TipoPlantilla.IdTipoPlantilla;
                            plantillas.TipoPlantilla.Nombre = obj.TipoPlantilla.Nombre.ToString();
                            /***Header Plantilla***/
                            plantillas.HeaderPlantilla = new ML.HeaderPlantilla();
                            plantillas.HeaderPlantilla.IdHeaderPlantilla = obj.HeaderPlantilla.IdHeaderPlantilla;                            
                            plantillas.HeaderPlantilla.Nombre = obj.HeaderPlantilla.Nombre;
                            plantillas.HeaderPlantilla.CodeHTML = obj.HeaderPlantilla.CodeHTML;
                            plantillas.HeaderPlantilla.ImagenFondo = obj.HeaderPlantilla.ImagenFondo;
                            plantillas.HeaderPlantilla.LogoIco = obj.HeaderPlantilla.LogoIco;
                            plantillas.HeaderPlantilla.color1 = obj.HeaderPlantilla.color1 != null ? obj.HeaderPlantilla.color1.ToString():"";
                            plantillas.HeaderPlantilla.color2 = obj.HeaderPlantilla.color2 != null ? obj.HeaderPlantilla.color2.ToString():"";
                            plantillas.HeaderPlantilla.LogoAlinea = new ML.Aling();
                            plantillas.HeaderPlantilla.LogoAlinea.IdAling = obj.HeaderPlantilla.Aling.IdAling;
                            plantillas.HeaderPlantilla.ImagenFondoAlinea = new ML.Aling();
                            plantillas.HeaderPlantilla.ImagenFondoAlinea.IdAling = obj.HeaderPlantilla.Aling1.IdAling;
                            /***Footer Plantilla***/
                            plantillas.FooterPlantilla = new ML.FooterPlantilla();
                            plantillas.FooterPlantilla.IdFooterPlantilla = obj.FooterPlantilla.IdFooterPlantilla;
                            //plantillas.FooterPlantilla.IdPlantillaDefinida =Convert.ToInt32(obj.FooterPlantilla.IdPlantillaDefinida);
                            plantillas.FooterPlantilla.Nombre = obj.FooterPlantilla.Nombre;
                            plantillas.FooterPlantilla.CodeHTML = obj.FooterPlantilla.CodeHTML;
                            plantillas.FooterPlantilla.ImagenFondo = obj.FooterPlantilla.ImagenFondo;
                            plantillas.FooterPlantilla.LogoIco = obj.FooterPlantilla.LogoIco;
                            plantillas.FooterPlantilla.Color1 = obj.FooterPlantilla.Color1 != null ? obj.FooterPlantilla.Color1.ToString():"";
                            plantillas.FooterPlantilla.Color2 = obj.FooterPlantilla.Color2 != null ? obj.FooterPlantilla.Color2.ToString():"";
                            plantillas.FooterPlantilla.LogoAlinea = new ML.Aling();
                            plantillas.FooterPlantilla.LogoAlinea.IdAling = obj.FooterPlantilla.Aling.IdAling;
                            plantillas.FooterPlantilla.ImagenFondoAlinea = new ML.Aling();
                            plantillas.FooterPlantilla.ImagenFondoAlinea.IdAling = obj.FooterPlantilla.Aling1.IdAling;
                            result.Objects.Add(plantillas);
                            result.EditaPlantillas = plantillas;
                            result.Correct = true;

                        }
                    }

                }
                
            }
            catch (Exception ex)
            {
                result.Correct = false;
                result.ErrorMessage = ex.Message;

            }
            return result;
        }

