        public static ML.Result AddPlantilla(ML.Plantillas plantilla)
        {
            ML.Result result = new ML.Result();
            using (DL.RH_DesEntities context = new DL.RH_DesEntities())
            {
                using (DbContextTransaction transaction = context.Database.BeginTransaction())
                {
                    try
                    {
                        if (plantilla.HeaderPlantilla.CodeHTML == null) { plantilla.HeaderPlantilla.CodeHTML = " "; }
                        if (plantilla.HeaderPlantilla.ImagenFondo == null) { plantilla.HeaderPlantilla.ImagenFondo = " "; }
                        if (plantilla.HeaderPlantilla.LogoIco == null) { plantilla.HeaderPlantilla.LogoIco = " "; }
                        if (plantilla.HeaderPlantilla.LogoAlinea == null) { plantilla.HeaderPlantilla.LogoAlinea = new ML.Aling(); plantilla.HeaderPlantilla.LogoAlinea.IdAling = 0; }
                        if (plantilla.HeaderPlantilla.ImagenFondoAlinea == null) { plantilla.HeaderPlantilla.ImagenFondoAlinea = new ML.Aling(); plantilla.HeaderPlantilla.ImagenFondoAlinea.IdAling = 0; }
