        public static ML.Result UpdatePlantilla(ML.Plantillas plantilla)
        {
            ML.Result result = new ML.Result();
            using (DL.RH_DesEntities context = new DL.RH_DesEntities())
            {
                using (DbContextTransaction transaction = context.Database.BeginTransaction())
                {
                    try
                    {
                        var UsuarioModificacion = plantilla.UsuarioModificacion == null ? "Usuario Editor" : plantilla.UsuarioModificacion;
                        var ProgramaModificacion = "Actualiza Plantilla";

                        DL.Plantillas updatePlantilla = context.Plantillas.FirstOrDefault(x => x.IdPlantilla == plantilla.IdPlantilla);
                        int idDetallePlantilla =Convert.ToInt32(updatePlantilla.IdDetallePlantilla);
                        int idBodyPlanti = Convert.ToInt32(updatePlantilla.IdBodyPlantilla);
                        int idHeaderPlantilla = Convert.ToInt32(updatePlantilla.IdHeaderPlantilla);
                        int idFooterPlantilla = Convert.ToInt32(updatePlantilla.IdFooterPlantilla);
                        //updatePlantilla.TipoPlantilla = new DL.TipoPlantilla();
                        //updatePlantilla.TipoPlantilla.IdTipoPlantilla = Convert.ToInt32(plantilla.TipoPlantilla.IdTipoPlantilla.ToString());                      
                        updatePlantilla.IdTipoPlantilla = plantilla.TipoPlantilla.IdTipoPlantilla;
                        updatePlantilla.Nombre = plantilla.Nombre;                        
                        updatePlantilla.UsuarioModificacion = UsuarioModificacion;
                        updatePlantilla.ProgramaModificacion = ProgramaModificacion;
                        updatePlantilla.FechaHoraModificacion = DateTime.Now;
                        context.SaveChanges();
                        DL.DetallePlantilla updatePlantillaDetalle = context.DetallePlantilla.FirstOrDefault(x => x.IdDetallePlantilla == idDetallePlantilla);                        
                        updatePlantillaDetalle.Nombre = updatePlantilla.Nombre;
                        updatePlantillaDetalle.Color1 = plantilla.DetallePlantilla.Color1;
                        updatePlantillaDetalle.Color2 = plantilla.DetallePlantilla.Color2;
                        updatePlantillaDetalle.ImagenIco = plantilla.DetallePlantilla.ImagenIco;
                        updatePlantillaDetalle.IdPlantillaDefinida = plantilla.DetallePlantilla.IdPlantillaDefinida;
                        updatePlantillaDetalle.UsuarioModificacion = UsuarioModificacion;
                        updatePlantillaDetalle.ProgramaModificacion = ProgramaModificacion;
                        updatePlantillaDetalle.FechaHoraModificacion = DateTime.Now;
                        context.SaveChanges();
                        DL.HeaderPlantilla updatePlantillaHeader = context.HeaderPlantilla.FirstOrDefault(x => x.IdHeaderPlantilla == idHeaderPlantilla);                        
                        updatePlantillaHeader.Nombre = plantilla.Nombre;                        
                        updatePlantillaHeader.LogoIco = plantilla.HeaderPlantilla.LogoIco;
                        updatePlantillaHeader.color1= plantilla.HeaderPlantilla.color1;
                        updatePlantillaHeader.color2 = plantilla.HeaderPlantilla.color2;
                        updatePlantillaHeader.ImagenFondo = plantilla.HeaderPlantilla.ImagenFondo;
                        updatePlantillaHeader.ImagenFondoAlinea = 1;// plantilla.HeaderPlantilla.ImagenFondoAlinea.IdAling;
                        updatePlantillaHeader.UsuarioModificacion = UsuarioModificacion;
                        updatePlantillaHeader.ProgramaModificacion = ProgramaModificacion;
                        updatePlantillaHeader.FechaHoraModificacion = DateTime.Now;
                        context.SaveChanges();
                        DL.FooterPlantilla updateFooterPlantilla = context.FooterPlantilla.FirstOrDefault(x => x.IdFooterPlantilla == idFooterPlantilla);
                        updateFooterPlantilla.Nombre= plantilla.Nombre;
                        updateFooterPlantilla.Color1 = plantilla.FooterPlantilla.Color1; ;
                        updateFooterPlantilla.Color2 = plantilla.FooterPlantilla.Color2;
                        updateFooterPlantilla.LogoIco = plantilla.FooterPlantilla.LogoIco;
                        updateFooterPlantilla.LogoAlinea = 1;//plantilla.FooterPlantilla.LogoAlinea.IdAling;
                        updateFooterPlantilla.ImagenFondo = plantilla.FooterPlantilla.ImagenFondo;
                        updateFooterPlantilla.ImagenFondoAlinea = 1; //plantilla.FooterPlantilla.ImagenFondoAlinea.IdAling;
                        updateFooterPlantilla.UsuarioModificacion = UsuarioModificacion;
                        updateFooterPlantilla.ProgramaModificacion = ProgramaModificacion;
                        updateFooterPlantilla.FechaHoraModificacion = DateTime.Now;
                        context.SaveChanges();

                       /****Actualiza Body compuesto HTML******/
                        string headerHtml = "", imagenFondoH = "", logoIcoH = "";
                        string detalleHtml = "", imagenIcoD = "";
                        string footerHtml = "", imagenFondoF = "", logoIcoF = "";
                        string bodyHtml = "";
                        headerHtml = plantilla.HeaderPlantilla.CodeHTML == null ? "" : plantilla.HeaderPlantilla.CodeHTML;
                        imagenFondoH = plantilla.HeaderPlantilla.ImagenFondo == null ? "": plantilla.HeaderPlantilla.ImagenFondo;
                        logoIcoH = plantilla.HeaderPlantilla.LogoIco == null ? "": plantilla.HeaderPlantilla.LogoIco;
                        detalleHtml = plantilla.DetallePlantilla.CodeHTML == null ? "": detalleHtml = plantilla.DetallePlantilla.CodeHTML;
                        imagenIcoD = plantilla.DetallePlantilla.ImagenIco == null ? "" : imagenIcoD = plantilla.DetallePlantilla.ImagenIco;
                        footerHtml = plantilla.FooterPlantilla.CodeHTML == null ? "": plantilla.FooterPlantilla.CodeHTML;
                        imagenFondoF = plantilla.FooterPlantilla.ImagenFondo == null ? "": plantilla.FooterPlantilla.ImagenFondo;
                        logoIcoF = plantilla.FooterPlantilla.LogoIco == null ?"": plantilla.FooterPlantilla.LogoIco;
                        if (headerHtml.Contains("***Logo***"))
                        { headerHtml = headerHtml.Replace("***Logo***", logoIcoH.ToString()); }
                        if (headerHtml.Contains("***imagenFondo***"))
                        { headerHtml = headerHtml.Replace("***imagenFondo***", imagenFondoH.ToString()); }
                        if (detalleHtml.Contains("***ImagenIco***"))
                        { detalleHtml = detalleHtml.Replace("***ImagenIco***", imagenIcoD.ToString()); }
                        if (footerHtml.Contains("***Logo***"))
                        { footerHtml = footerHtml.Replace("***Logo***", logoIcoF.ToString()); }
                        if (footerHtml.Contains("***imagenFondo***"))
                        { footerHtml = footerHtml.Replace("***imagenFondo***", imagenFondoF.ToString()); }
                        bodyHtml = headerHtml + detalleHtml + footerHtml;
                        DL.BodyPlantilla updatePlantillaBody = context.BodyPlantilla.FirstOrDefault(x => x.IdBodyPlantilla == updatePlantilla.IdBodyPlantilla);
                        updatePlantillaBody.Nombre = plantilla.Nombre;
                        updatePlantillaBody.Color1 = plantilla.BodyPlantilla.Color1;
                        updatePlantillaBody.Color2 = plantilla.BodyPlantilla.Color2;
                        updatePlantillaBody.ImagenFondo = plantilla.BodyPlantilla.ImagenFondo;
                        updatePlantillaBody.CodeHTML = bodyHtml;
                        updatePlantillaBody.UsuarioModificacion = UsuarioModificacion;
                        updatePlantillaBody.ProgramaModificacion = ProgramaModificacion;
                        updatePlantillaBody.FechaHoraModificacion = DateTime.Now;
                        context.SaveChanges();
                        transaction.Commit();
                        result.Correct = true;
                    }
                    catch (Exception aE)
                    {
                        transaction.Rollback();
                        result.Correct = false;
                        result.ErrorMessage = aE.Message;
                    }
                    return result;
                }
            }

        }

        public static ML.Result DeletePlantilla(int IdPlantilla)
        {
            ML.Result result = new ML.Result();
            using (DL.RH_DesEntities context = new DL.RH_DesEntities())
            {
                using (DbContextTransaction transaction = context.Database.BeginTransaction())
                {
                    try
                    {
                        DL.Plantillas deletePlantilla = context.Plantillas.FirstOrDefault(x => x.IdPlantilla == IdPlantilla);
                        deletePlantilla.IdEstatus = 3;
                        context.SaveChanges();
                        transaction.Commit();
                        result.Correct = true;

                    }
                    catch (Exception aE)
                    {
                        transaction.Rollback();
